INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100002', 'skill_tagger', 'Tag and categorize skills from stories', 'Tag skills mentioned in your stories. Use the Skills Library for reference.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100003', 'ranking_grid', 'Drag-and-drop grid for ranking items 1-10', 'Compare items pairwise to establish priority order from 1-10.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100004', 'mbti_selector', '4-letter personality type selector', 'Enter your 4-letter personality code from 16personalities.com.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100005', 'budget_calculator', 'Monthly/annual budget calculator with BATNA', 'Enter your monthly expenses to calculate annual needs and minimum hourly rate.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100006', 'flow_tracker', 'Daily tracker for energy and focus levels on activities', 'Rate each activity on energy (-2 to +2) and focus level. Mark high-flow activities.', 1, 'daily', 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100007', 'life_dashboard', 'Dashboard for rating life areas (work, play, love, health)', 'Rate each life area (work, play, love, health) from 1-10 and add notes.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100008', 'mindset_profiles', 'Display mindset profile information', 'Review the five designer mindsets and select characters that represent each.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100009', 'failure_reframer', 'Tool for reframing failures as learning opportunities', 'Describe a failure, then reframe it as a learning opportunity.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100010', 'idea_tree', 'Visual brainstorming tree structure', 'Start with a central word and branch out with related ideas and possibilities.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100011', 'job_combiner', 'Combine ideas into potential job descriptions', 'Combine elements from your idea trees into potential job descriptions.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100012', 'career_timeline', '5-year career timeline planner', 'Plot key milestones for the next 5 years including work and life events.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100013', 'career_assessment', 'Career option scoring and comparison', 'Score this career option on coherence, work needs, life needs, and unknowns.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100014', 'job_analyzer', 'Analyze job descriptions for keywords', 'Paste job descriptions to extract keywords, requirements, and patterns.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100015', 'credential_writer', 'Rewrite credentials using employer language', 'Rewrite your experience using keywords and language from job descriptions.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100016', 'resume_builder', 'Structured resume building tool', 'Build your resume section by section using your prepared content.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100017', 'company_tracker', 'Track target companies', 'Maintain your list of target companies with status and notes.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100018', 'company_researcher', 'Company research template', 'Research and document key information about target companies.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100019', 'research_tracker', 'Track research progress', 'Track your research progress across companies.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100020', 'contact_tracker', 'Track networking contacts', 'Track contacts at each company with relationship status.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100021', 'networking_tracker', 'Track networking activities', 'Log daily networking activities and connection requests.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100022', 'outreach_tracker', 'Track outreach attempts and responses', 'Log outreach attempts, responses, and follow-up actions.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100023', 'meeting_prep', 'Meeting preparation template', 'Prepare for meetings with research, questions, and talking points.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100024', 'meeting_notes', 'Meeting notes template', 'Capture key points, action items, and follow-ups from meetings.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100025', 'referral_tracker', 'Track referrals received', 'Track referrals received and their outcomes.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100026', 'opportunity_tracker', 'Track job opportunities', 'Track job opportunities through the application pipeline.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100027', 'bucketing_tool', 'Drag-and-drop sorting into ranked buckets', 'Drag items from a list into 5 vertical buckets representing frequency of use, from most to least.', 0, NULL, 1, 1);
INSERT INTO tools (id, name, description, instructions, has_reminder, reminder_frequency, version, is_active) VALUES ('100028', 'competency_assessment', 'Self-assessment against OECD competency framework', 'Rate yourself on 15 competencies across Delivery, Interpersonal, and Strategic categories at 5 proficiency levels.', 0, NULL, 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100000', 'heading', 'Part 1:
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100001', 'heading', 'Table of Contents
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100002', 'instruction', 'Work is anything related to the creation of value. Work is any economic activity, regardless of your compensation for that activity. Any activities you engage in with the intent of attaining some goal is work. Doing chores, volunteering, earning degrees, and sales are all to be considered work.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100003', 'instruction', 'Play is nearly the opposite. Play is any activity you engage in for the joy of doing it. If you are competing or trying to accomplish something, then it is not play. Play is what most young children do, without a worry in the world, motivated only by the joy that will come. Of the four parts of life, most adults are worst at play.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100004', 'instruction', 'Love is mostly for you to define. I cannot, and will not, advise you on matters of love. If you so choose, there are plenty of other resources on the topic. Nonetheless, it is vital for every person and should be considered when planning your future.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100005', 'instruction', 'Traditionally, health is broken into three categories: physical, mental, and spiritual. To reach the greatest heights, care for all three of these areas. I suggest you consider and look after your physical and spiritual health on your own. There are many resources available in the world to answer any questions you have about physical and spiritual help. Everyone has different physical and spiritual needs, so the exercises here focus on mental health. The importance of health cannot be overstated.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100006', 'instruction', 'Understanding all four of these parts of your life will help clarify the path in front of you, not to mention how useful it will be to have all of this information in one place. Save your interpretations for later.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100007', 'instruction', 'Remember, our goal is that this is the last time you ever have to fill all this information out in full. Life demands updates, but power through - the juice really is worth the squeeze.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100008', 'instruction', 'You can only ever be exactly where you are, so let’s figure out where you’re rooted….', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100009', 'heading', 'Module 1 – Work Factors 1', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100010', 'heading', 'Skills and Talents', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100011', 'instruction', 'Skills are the most basic unit of performance. Most people think their skills are limited to specific work situations. The goal of this exercise is for you to take full stock of all of your skills, take note of the ones you enjoy the most, and to present them professionally.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100012', 'instruction', 'According to researcher Sydney Fine, skills can be broken down into three categories: Knowledges, Self-management, and Transferable Skills.²', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100013', 'instruction', '-Knowledges are the topics you know, generally nouns.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100014', 'instruction', 'Spanish, chemical engineering, graphic design.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100015', 'instruction', '-Self-management skills describe the way you do your work, generally adjectives/adverbs.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100016', 'instruction', 'Relentless, innovative, meticulous.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100017', 'instruction', '-Transferable skills are the things you can do, generally verbs.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100018', 'instruction', 'Teach, paint, repair, negotiate.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100019', 'instruction', 'Knowledge is knowing a second language. Self-Management is how often and diligently you practice. Transferable skill is using your second language to some benefit.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100020', 'instruction', 'All of these are important.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100021', 'instruction', 'First, we want active verbs describing work you can do.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100022', 'instruction', '→ extensive List of Skills for ideas ←', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100023', 'heading', 'I    Transferable Skills', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100024', 'instruction', '1. Record any talent you have used professionally. This is your experience', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100025', 'instruction', 'a) List every job you have had, every significant project you have completed. Write down every course, program, training, and degree you have completed or are currently working through.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100026', 'instruction', 'b) For each item from part a), write down everything you had to do.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100027', 'instruction', 'c) Every task you completed is a skill you have. Let''s lock these in and grade your mastery of those skills. Don''t fret over this, trust your gut and move through them. None of this defines your world, we''re just building a profile', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100028', 'instruction', '2. Think of a time you overcame a challenge (inside or outside of work), a time you soared, for each of the following statements. One challenge per statement.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100029', 'instruction', 'a) A time when you had an impact', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100030', 'instruction', 'b) A time you did something no one else around could have done', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100031', 'instruction', 'c) A time when you were genuinely excited', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100032', 'instruction', 'd) A time when you impressed yourself', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100033', 'instruction', 'e) A time when everything went according to plan', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100034', 'instruction', 'f) A time when nothing went according to plan', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100035', 'instruction', 'g) A time when you loved what you were doing', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100036', 'instruction', 'h) A time you did something you would like to do again', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100037', 'instruction', 'i) A time you did something you would not normally do', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100038', 'instruction', 'j) Any other story which represents your skillset', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100039', 'instruction', '3. Choose the three (3) most impactful stories and describe these events using the anagram SOARED.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100040', 'instruction', 'Situation – describe the situation', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100041', 'instruction', 'Obstacle – describe the obstacle/problem you faced', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100042', 'instruction', 'Action – describe your action: the step by step process you took to address the obstacle', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100043', 'instruction', 'Result – describe the result of your actions', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100044', 'instruction', 'Evaluation – your self evaluation and/or how higher-ups received your actions', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100045', 'instruction', 'Discovery – describe what you discovered from the process', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100046', 'instruction', '4.  Record every transferable skill you used in each of your stories. Refer to the List of Skills for reference.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100047', 'instruction', '5. Organize your skillset from the most used to the least used.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100048', 'instruction', '6. These are the 10 or so skills you have mastered. Adjust mastery above if you feel like these are off or we are missing something?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100049', 'instruction', '7. Use the ranking grid to organize these skills by importance to you.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100050', 'heading', 'II    Self Management Skills', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100051', 'instruction', 'These are often referred to as Soft Skills. You can find lists of common soft skills that random internet people claim will get you any job. Describing ourselves can be difficult. Doing so originally and authentically can be even more challenging.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100052', 'instruction', 'Self Identification Basics:', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100053', 'instruction', 'The Enneagram, Myers-Briggs, the Big 5, and the Self Directed Search are the most commonly used personality tests. Each has its strengths and weaknesses, and are more alike than they are different.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100054', 'instruction', 'We will be using an adaptation of Myers-Briggs as a tool to help us learn about ourselves. It will help you frame your personality in a way which is easier to understand and give you a starting place for finding creative ways to describe yourself. I do not put my full faith into any skills test for many reasons, so remember, this is only a tool. No personality test is perfect. We will use an adaptation of the Myers-Briggs to give you a starting point for your Self-Inventory.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100055', 'instruction', 'No personality test is perfect. We will use an adaptation of the Myers-Briggs to give you a starting point for your Self-Inventory.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100056', 'instruction', 'The questionnaire will assign you a four-letter code upon completion. Each letter represents your bias on a set of four scales.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100057', 'instruction', 'Extroversion (E) vs. Introversion (I)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100058', 'instruction', 'Sensing (S) vs. Intuition (N)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100059', 'instruction', 'Thinking (T) vs. Feeling (F)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100060', 'instruction', 'Judging (J) vs. Perceiving (P)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100061', 'instruction', 'The purpose of this exercise is to illuminate your character traits and to open your mind to possibilities.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100062', 'instruction', 'While taking the test, it is essential to answer the questions honestly, as they apply to you. There are no wrong answers. Although some of your responses may make you slightly uncomfortable, it is crucial to be as honest as you are able. The more accurate you are, the more this tool can help you.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100063', 'instruction', 'Few people fit their type perfectly. Do not let the results define you. Instead, consider the truths and make a note of the differences.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100064', 'instruction', 'www.16personalities.com⁴', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100065', 'instruction', 'Write your four letter MBTI code here:', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100066', 'instruction', '(The website where you take the test is a useful resource, feel free to learn about your type from them, but it is not necessary for this program.  We don''t sponsor each other in any way.)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100067', 'instruction', 'To be more confident, read through each of these relative dichotomies and mark yourself on each scale below.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100068', 'instruction', 'Extraverts are more likely to be energized by company, enjoy being the center of attention, keep a fast pace, and talk more than they listen.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100069', 'instruction', 'Introverts are more likely to be energized by alone time, be more private, keep their thoughts to themselves, and listen before speaking.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100070', 'instruction', 'Sensors tend to be concerned with certainty and what is realistic. They tend to be more literal and are oriented towards the present situation.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100071', 'instruction', 'Intuitives tend to be concerned with innovation and what is possible. They tend to be more figurative and are oriented towards the future.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100072', 'instruction', 'Thinkers are likely to be most concerned with logic, applying impersonal analysis to opportunities and decisions. They can be seen as detached or heartless.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100073', 'instruction', 'Feelers are likely to be most concerned with harmony, empathizing with involved parties with tact when making decisions. May be seen as overemotional, valuing all feelings equally.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100074', 'instruction', 'Judgers tend to derive the most satisfaction with the completion of a task and manage their time as a finite resource.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100075', 'instruction', 'Perceivers prefer to keep their options open and thrive when adapting to new situations. They often view time and deadlines as elastic.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100076', 'instruction', 'Remember, no test can tell you who you are, but these tools can help you describe your talents and identify your weaknesses.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100077', 'instruction', 'You can dig into 16personalities to find some self management skills that may, or may not apply to you.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100078', 'instruction', '1. Review these. Record any characteristics, strengths, and weaknesses which describe you here.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100079', 'instruction', 'a) Characteristics', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100080', 'instruction', '{list_builder}{prompt with skills associated with type}', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100081', 'instruction', 'b) Strengths', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100082', 'instruction', '{list_builder}{prompt with skills associated with type}', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100083', 'instruction', 'c) Weaknesses', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100084', 'instruction', '{list_builder}{prompt with skills associated with type}', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100085', 'instruction', '2. These are the remaining characteristics, strengths, or weaknesses which match your MBTI type, but you didn''t select above. Reflect and explain why they are false.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100086', 'instruction', '3. Choose your ten favorite soft skills from above', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100087', 'instruction', 'Let''s rank these --', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100088', 'instruction', '4. Use your Self Management Skills to accurately describe your top ten Transferable Skills.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100089', 'instruction', '5. Make a note of times you used your skills in the way you describe.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100090', 'instruction', '(possibly from your written stories)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100091', 'instruction', '6. Add HONEST hard figures to your descriptions. Numbers. Data. Records. Accomplishments. They must be HONEST. You do not want to lie or exaggerate.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100092', 'heading', 'Knowledges', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100093', 'instruction', 'Now dive into what you know. The subject matter, the processes, your interests, everything. It is imperative to include everything you know here (or at least as much as possible).', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100094', 'instruction', '-Knowledges are the things you know, generally nouns.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100095', 'instruction', 'Spanish, chemical engineering, graphic design', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100096', 'instruction', 'From work. From your education.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100097', 'instruction', 'From your hobbies. From your interests.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100098', 'instruction', 'From any of your groups or clubs.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100099', 'instruction', 'If it comes to mind, write it down.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100100', 'instruction', 'You want to start broad. Take stock of everything first.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100101', 'instruction', 'Then grade them according to Mastery.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100102', 'instruction', '1. a) Write every skill, process, or task you had to perform for those jobs.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100103', 'instruction', '2. a) Write every skill, process, or task you had to perform pursuing education.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100104', 'instruction', '3. Knowledge is far reaching, likely further than what you have considered before in a professional manner, let''s stretch your mind a bit to find what else you know....', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100105', 'instruction', 'a) What are your favorite hobbies?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100106', 'instruction', 'b) What do you research in your free time?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100107', 'instruction', 'c) If you wrote a book, what would the content be?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100108', 'instruction', 'd) What tasks or activities seem to make time fly?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100109', 'instruction', '4. What fields, careers, or industries sound interesting to you? Check the personality resource for ideas.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100110', 'instruction', '5. Grade each knowledge on Mastery.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100111', 'heading', 'Module 2 – Work Factors 2', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100112', 'heading', 'Environment', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100113', 'instruction', '"Location, location, location" - every realtor ever', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100114', 'instruction', '[DEPRECATED - merged into 100113]', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100115', 'instruction', '[DEPRECATED - merged into 100113]', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100116', 'instruction', 'There are two types of location. Micro and Macro', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100117', 'instruction', 'Macro – where in the world do you want to be? Do you want to stay in your town or city? Or would you rather be somewhere else? Are there specific activities you wish to have access to? Do you need to live in a city? Or do you prefer the quiet life of the country?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100118', 'instruction', 'Micro – what should your work environment be? Do you need to spend time outside? Is it necessary that you work near a park? Or do you prefer to spend your time indoors? What are your preferred working conditions?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100119', 'instruction', 'Of course, the more flexible you are, the more opportunities you will find. Also, generally speaking, where there are more people, there are more opportunities. Still, it is essential to know your preferences; you might find your dream job in your dream location. There is only one way to find out.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100120', 'instruction', 'Do this exercise even if you feel you already know where you want to be. First of all, you might surprise yourself. If not, then you have found more evidence to support what you already thought.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100121', 'heading', 'I    Macro: Where in the World...', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100122', 'instruction', 'Do you know where you want to live?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100123', 'instruction', 'a) If you do, write that here. If not, write down the characteristics of a place that you might want to live. (ex. by the ocean, has an active nightlife, quiet, access to nature, etc.)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100124', 'instruction', 'b) What are your location restraints?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100125', 'instruction', 'c) Do you need to live in a specific country?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100126', 'instruction', 'd) Do you need to stay close to your family?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100127', 'instruction', 'e) Do you have medical needs?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100128', 'instruction', 'Struggling for ideas? Even if you are 100% certain, do the following:', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100129', 'instruction', 'a) Write down every place you have lived.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100130', 'instruction', 'b) List every single thing you disliked about those places. (What does it have? What does it lack? What type of people are there? What feelings does it give you?)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100131', 'instruction', 'c) List the opposites of each of the location traits you dislike. Also, list any positive traits that come to mind.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100132', 'instruction', 'd) Rank the positive location traits.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100133', 'instruction', 'Research. Open your mind to possibilities. Find as many good ideas as possible. Look for places that meet your needs and have as many of your top 5 location traits as possible.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100134', 'instruction', 'a) Search the Internet.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100135', 'instruction', '-Search for places which excel in each of your desired traits. Make lists.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100136', 'instruction', '-Are there any which excel in multiple?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100137', 'instruction', '-Find 5-10 places which make you excited. Record what excites you about each location.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100138', 'instruction', 'b) Ask around. Family, friends, colleagues, acquaintances, you never know what treasure they hold until you ask.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100139', 'instruction', '-Write down your ranked list of location traits and ask 10-20 people if they know of anywhere which has those traits. Write down their ideas. ALWAYS thank them for their time and effort.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100140', 'instruction', '-Are there any locations which excel in multiple traits?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100141', 'instruction', '-Research each of the locations which embody multiple of your desired traits.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100142', 'instruction', '-Find 5-10 places which make you excited. Record what excites you about each location.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100143', 'instruction', 'Narrow down your two lists to a single list of 10 possible locations. Rank those ten from most exciting down.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100144', 'heading', 'IF YOU HAVE A PARTNER', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100145', 'instruction', '– Before you make a list of locations – Before you start researching –', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100146', 'instruction', '-Both of you do numbers 1 & 2 separately.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100147', 'instruction', '-Discuss your preferred location traits. Find any similarities.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100148', 'instruction', '-Rank your combined traits. (combine the top 5 from each partner)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100149', 'instruction', '-Then both of you complete number 3', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100150', 'instruction', '-Do number 4 together. Compare your findings. Discuss honestly. Agree upon ten possible locations. Rank together.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100151', 'instruction', 'Pay the most attention to, make a special note of, your top 3 locations.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100152', 'heading', 'II    Micro: the Work Environment You Flourish in', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100153', 'instruction', 'Now we will focus on the look and feel of the workplace.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100154', 'instruction', 'For each of the following, consider each productive space you have ever had. You may include classrooms, a desk at home, a library, or a cafe. Any space where you get work done is worth studying.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100155', 'heading', 'Previous Employment', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100156', 'instruction', 'a) List every job you have had.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100157', 'instruction', 'b) List every work condition which had a positive impact upon you, your mood, or your effectiveness.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100158', 'instruction', 'c) List every work condition which had a negative impact upon you, your mood, or your effectiveness.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100159', 'instruction', 'd) What would the opposite of those negative influences be?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100160', 'instruction', 'Consider the above. Describe your ideal workplace.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100161', 'instruction', 'a) How does the office look?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100162', 'instruction', 'b) What is nearby?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100163', 'instruction', 'c) Are there any important features?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100164', 'instruction', 'd) What part of the city or town is best?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100165', 'instruction', 'e) Is it near child care?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100166', 'instruction', 'f) What else can you think of?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100167', 'heading', 'People', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100168', 'instruction', 'For many, we spend more time with the people we work with than the people we live with. For that reason, aim to have a clear idea of whom you work well with, what types of people can help you to be more productive, and what kinds of people have a negative influence on your work and life.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100169', 'instruction', 'Simply understanding these things does not guarantee that you will love your next co-workers, but it certainly helps to know what you are looking for.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100170', 'instruction', 'It is also essential to understand the type of people you like to work for. You work for your boss, and you work for your client, customer, pupil, audience, family, organization, etc. You work for the people who depend on you to do your job.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100171', 'instruction', 'Some people thrive in competitive environments while others need emotional support. Some people need specific direction, and others need to be left to their expertise. Some people work extraordinarily well in groups, and others prefer solitary labor.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100172', 'instruction', 'This exercise is designed to help you identify the sorts of people you are productive with and for, and those you are not.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100173', 'instruction', 'Refer to your complete list of experiences from Module 1.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100174', 'instruction', 'Review each job or experience you have had. Recall and record the traits of the people who hurt your productivity in any way. Maybe you didn''t like them, but do not focus on who they are. You need to pinpoint what about them made your work less productive. List the harmful character traits.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100175', 'instruction', 'Write the antonym of each of the traits from number 1.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100176', 'instruction', 'Review each job or experience you have had. Recall and record the traits of the people who helped your productivity in any way.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100177', 'instruction', 'Recall any time when you were particularly productive. The production can be from your education, the stories you wrote before, or work experience. Who was directing you? Were you serving anyone (client, etc.)?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100178', 'instruction', 'What traits do the people from 4 have that support a positive work environment?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100179', 'instruction', 'What types of people do you want to work with?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100180', 'instruction', 'Do you prefer to work in teams, pairs, or alone?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100181', 'instruction', 'What types of people do you want to work for?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100182', 'instruction', 'Who in this world do you want to serve with your work?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100183', 'heading', 'Compensation', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100184', 'instruction', 'How much money do you want to make?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100185', 'instruction', 'How much money do you need to make?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100186', 'instruction', 'What are your needs?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100187', 'instruction', 'How much do you plan to save?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100188', 'instruction', 'What bonuses and extras are you seeking from your work?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100189', 'instruction', 'Record how much you have made at each of your positions, including any changes in pay at any one position.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100190', 'instruction', 'Write a complete budget.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100191', 'instruction', 'Be diligent about your budget and live within your means. Avoid accumulating unnecessary debt. It is easy to spend money; it is difficult to save money. Wealth is built by saving and investing wisely.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100192', 'instruction', 'What benefits do you need?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100193', 'instruction', 'Describe the benefits and drawbacks of each, for you. (assume bi-weekly or monthly paydays)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100194', 'instruction', '– Salary (aggregate pay per year or month, regardless of hours worked)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100195', 'instruction', '– Hourly (pay for time worked)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100196', 'instruction', '– Contracting (pay agreed upon per project, typical for freelancers)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100197', 'instruction', '– Commission (% of sale per sale)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100198', 'instruction', '– Combination of above, or Other (specify)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100199', 'heading', 'Module 3 – Priorities, Ecstasy & Flow', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100200', 'instruction', 'Prioritize', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100201', 'instruction', 'Before we move on, you need to prioritize your work factors from Modules 1 & 2.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100202', 'instruction', 'When considering employment, what is most important to you?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100203', 'instruction', 'Compensation – how much money you earn, benefits, and bonus structure', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100204', 'instruction', 'Environment – where in the world you live and the make-up of your office', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100205', 'instruction', 'Skills – being able to use the skills you have or learn new skills', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100206', 'instruction', 'Knowledges – being able to excel in your field of interest or expertise', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100207', 'instruction', 'People – getting along with the people you work with and for', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100208', 'instruction', 'If you feel the need to be more specific, do so here.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100209', 'instruction', 'Flow', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100210', 'instruction', 'You have documented all of your work factors and have thought about what is most important to you.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100211', 'instruction', 'Now, I want you to recall the time when you were the most present. For this step, you will reflect upon your past experiences to get a sense of how they affected you and your fundamental motivation. I am talking about the sensation of flow.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100212', 'instruction', 'Human beings are hardwired to experience flow. During states of flow, people are upwards of 500% more effective or productive doing their work.⁵ In short, the body achieves this by turning off the prefrontal cortex, effectively allowing the brain to remove criticism and doubt, exist deeply in the present moment, make more lateral connections between new and old information, and innovate upon those newly created ideas seamlessly. The state is often described by the feeling of brilliant information flowing out of the subject, connecting one moment seamlessly to the next.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100213', 'instruction', 'Everyone experiences flow from time to time, the most brilliant among us learn to use it to their advantage. You can increase your chances of flow by practicing mindful paced breathing. There are several books and entire businesses built around that fact, so I will spare you the details for now. Practice breathing evenly ("hoooooooooooo", not "ho ho ho ho ho ho ho") and in rhythm (try in for 5, out for 5). You may have been taught to breathe from your gut, but I want you to breathe from the core of your chest, more specifically your heart. There is a concept called entrainment. Entrainment is the phenomena of rhythmic alignment; it occurs across physics, biology and social interactions. Training your nervous system to breathe from your core, you can entrain your physiology on your heart. Dive into this rabbit hole if it piques your interest, but the basic concept is that by eliminating chaos, you benefit from the calm power of harmony at a physiological level. Steady breath will help your heart beat more regularly, which will entrain the rest of your body to the same rhythm. When your body is all in rhythm, it resonates, creating a whole that is more than the sum of its parts. Practice breathing to increase your vitality and the amount of time you are in flow.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100214', 'instruction', 'There is a great deal of research about the idea of "flow".⁵ Every expertise has its own word to describe the state, most people recognize "in the zone". Flow is often achieved when someone is pushed to their extreme limits after preparation. These are times when all of our skills and expertise are called upon, we shut out everything except the task at hand, and we execute on a very high level. Symptoms of flow include time distortion, being oblivious to anything other than the task, euphoria, extreme productivity, creative ease, and a sense of singular focus. If you have ever amazed yourself with what you could do or you accomplished something that did not seem possible at the start, you were probably experiencing Flow.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100215', 'instruction', 'Flow is similar to a sense of ecstasy, but ecstasy is more closely related to play than work. Ecstasy describes also being singular in mind and focus, being exactly where and when you are, but it refers to elation and bliss rather than accomplishment and execution. Some professionals differentiate between what we get joy from, and the work that we do. The best way to live life is to align the two, just like our life values and our work values. Ecstasy is pure happiness, pure energy. Flow is the essence of the creative state. Physiologically, the two are more or less the same; these extreme mental states are characterized by a flood of powerful neurotransmitters.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100216', 'instruction', 'Separate flow into focus and energy for the purpose of picking your brain during this activity.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100217', 'instruction', 'Think of focus as being the scale between boring and captivating.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100218', 'instruction', 'Think of energy as being the scale between draining and energizing.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100219', 'instruction', 'When you are fully engaged and completely energized by a task, you are experiencing flow.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100220', 'instruction', 'Example: Stacy is a journalist who reported a huge corruption case a few years back. While doing the background research was a bit draining, she remembers being extremely focused. Then, there was one day when the whole story came together and she knew it was big. Once she had done her research she spread out all her findings one night to try to crack the case. She can''t quite say how she connected the dots, the memory is a bit blurry, but everything lined up for her as if she already knew all the answers; that was flow.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100221', 'heading', 'Memorable Past', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100222', 'instruction', 'Recall all the significant events you have recorded in the first two modules of this program. For each event, assign two scores: one for focus and one for energy.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100223', 'instruction', 'The most significant events, when you experienced flow, deserve more attention. Mark those events with the word "Flow".', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100224', 'instruction', 'Energy: draining (-2, -1, 0, 1, 2,) energizing', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100225', 'instruction', 'Focus: boring (v, w, x, y, z,) captivating', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100226', 'instruction', 'Example: For Stacy, researching her case was a (-1, z) while the night of creative problem solving was (2,z) and induced flow.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100227', 'instruction', 'Record your top 5-10 most focused activities.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100228', 'instruction', 'Record your top 5-10 most energizing activities.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100229', 'instruction', 'Is there any activity, task, or skill which you find both highly energizing and highly captivating?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100230', 'heading', 'Recent Past', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100231', 'instruction', 'Recall the past 3-10 days. (the more you do, the stronger the tool will be)', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100232', 'instruction', 'Record every significant activity of each day.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100233', 'instruction', 'Write a short description of each activity on the lines provided.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100234', 'instruction', 'Shade the boxes according to the energy and focus of the activity.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100235', 'instruction', 'Are there any activities which you find both highly energizing and highly captivating?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100236', 'heading', 'Present', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100237', 'instruction', 'Repeat this process at the end of each day. Continue until Part 3.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100238', 'instruction', 'Record every significant activity of the day.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100239', 'instruction', 'Write a short description of each activity on the lines provided.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100240', 'instruction', 'Shade the boxes according to the energy and captivation of the activity.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100241', 'instruction', 'Are there any activities which you find both highly energizing and highly captivating?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100242', 'heading', 'Module 4 – Love', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100243', 'instruction', 'The focus of this module is love. I think all people have a sense of what love is. You might be more comfortable considering the things and people you care about.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100244', 'instruction', 'Love can be a tricky topic to talk about. Love manifests differently in each person''s life. You might have intensely positive, intensely negative, or completely neutral experiences in each of the categories below. The purpose of this activity is not to dictate right from wrong, but to reflect and understand.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100245', 'instruction', 'Many programs focus on the idea of passion. Passion is simply one of the many paths into flow. Passion can be elusive; about 1:4 people know their passion. For our purposes, we will focus on what we love and care about as a window into what might bring us the most fulfillment in the future.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100246', 'instruction', 'There are no wrong answers here, these questions are for you and you only. While you will be asked questions that relate to this topic, we encrypt this data for you so that you are the only one with access to anything you write here; share what you are comfortable sharing.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100247', 'instruction', 'Answer every question. The process works, if you work it. This section is no less important than any other section. In fact, this might be one of the most important modules in the program. Seriously reflect on each of these, and record vivid details. Consider telling your stories using the acronym SOARED (wink wink) so that you don''t miss any important details.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100248', 'instruction', 'Don''t leave anything out. Some parts of your life might seem obvious or redundant, record them anyways. You cannot possibly be over prepared. Do the work.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100249', 'heading', 'Family', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100250', 'instruction', 'What stories from your childhood have helped shape who you are today?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100251', 'instruction', 'What family relationships have had particularly significant impacts upon your beliefs?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100252', 'instruction', 'Do you have any elderly family members you care for or support? How does this shape your needs?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100253', 'instruction', 'Do you have any children you care for or support? How does this shape your needs?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100254', 'instruction', 'Do you have any family members or stories which inspire you? If so, give details.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100255', 'instruction', 'What are the most significant lessons your family has taught you?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100256', 'heading', 'Community', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100257', 'instruction', 'What group, or groups, do you identify with?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100258', 'instruction', 'Are you involved in any organizations, religious groups, neighborhood associations, group of friends, gaming club, or other regularly organized social events?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100259', 'instruction', 'Why are you involved in those groups?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100260', 'instruction', 'Do you have any personal goals concerning your community?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100261', 'instruction', 'Is there anything that might cause you to leave your community?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100262', 'heading', 'World', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100263', 'instruction', 'Are there any worldly issues that are important to you? What are they?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100264', 'instruction', 'What are some lessons people should learn from history?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100265', 'instruction', 'Are there any worldly problems which you feel the need to help solve?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100266', 'instruction', 'Have you ever traveled outside of your country? Your continent?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100267', 'instruction', 'What have you learned from other cultures of the world?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100268', 'instruction', 'What do you think the world will look like in 10 years?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100269', 'instruction', '… 50 years?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100270', 'instruction', '… 100 years?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100271', 'heading', 'Self', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100272', 'instruction', 'They say that you are your own hardest critic. Critique yourself. What can you do better?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100273', 'instruction', 'What are your strengths?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100274', 'instruction', 'Do you have an idea or memory which makes you feel most like yourself? If so, describe it.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100275', 'instruction', 'What do you love about yourself?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100276', 'instruction', 'What major life events have shaped you? Briefly describe them.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100277', 'instruction', 'What is your greatest gift?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100278', 'instruction', 'Are you willing to do what needs to be done?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100279', 'heading', 'Module 5 – Health', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100280', 'instruction', 'We will focus on mental health. Physical and Spiritual health are both important, but I feel it is best for you to find answers to questions you may have about those topics from another source. People from all walks of life can benefit from mental health.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100281', 'instruction', 'I am going to address three aspects of mental health: achievement, fortitude, and emotional intelligence.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100282', 'instruction', 'In all three, the lesson is discipline and practice. The goal is not perfection. The goal is active practice.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100283', 'instruction', 'Bill Burnett describes five mindsets which are imperative to our cause. Curiosity, bias to action, reframing, awareness, and radical collaboration.¹ Mastering these mindsets and living them will lead you to opportunity.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100284', 'heading', 'Sharpening', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100285', 'instruction', 'Achievement is characterized by the things that we can do. Otherwise known as our skills. In previous modules we described our skills, our mastery over them, whether they give or take energy from us, and how captivated we are by them.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100286', 'instruction', 'Whether it is for our own pleasure, to further our working cause, or to give us a competitive edge, it is crucial to be a student of life, for life. Everybody should always be improving themselves in one way or another.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100287', 'instruction', 'The post-industrial economy is based on specialization. Pick a transferable skill or a knowledge which sets you apart from others, an expertise. If you do not have one, your duty is to make one of your skills an expertise.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100288', 'instruction', 'Additional practice, above and beyond what is needed for any of the work that you do is self-improvement for the sake of self-improvement. Sharpen your skills to make you a better candidate than your competition.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100289', 'instruction', 'You will want to start as soon as possible. Today is best; right now is even better.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100290', 'instruction', 'Choose a skill to practice.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100291', 'instruction', 'Dedicate at least 15 minutes of singular focus to improving that skill every single day.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100292', 'instruction', 'Practice the skill every single day. Don''t stop.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100293', 'instruction', 'If you grow tired of practicing this skill, reach a level of mastery, or otherwise, choose another skill and repeat the process. This is the never-ending task of self-betterment.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100294', 'heading', 'Grittiness', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100295', 'instruction', 'Grit describes the mental fortitude to work tirelessly for rewards which are either abstract or far removed, often both.⁶ I am a proponent of working smarter, not harder, but there is no replacement for diligence, perseverance, and drive. You absolutely must take yourself seriously. Even if your profession is in comedy, be a comic, not a joke.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100296', 'instruction', 'To get this message across, I have decided to share a personal story.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100297', 'heading', 'Take Yourself Seriously', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100298', 'instruction', 'On April 1st, 2016, I woke up at 3 pm. I had slept through two strategy meetings, an interview, training, and was supposed to be getting ready to go out knocking doors for the next 5 hours. I called my campaign director, she was furious, but more, she was sad.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100299', 'instruction', 'Four months ago she had put her name on the line to bring me in to direct a field canvass that had, until that point, no direction and no hope. Enthusiastic and passionate I left everything else behind to join her. We had been working 60+ hours each week to get going and had finally started to gain some momentum.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100300', 'instruction', 'Tuesday, March 31st, we went to dinner and then to a local bar to watch some music. Long after my co-worker went home, I was the last patron in the bar, finishing something past my 20th drink, around 4 am. I had promised to be at work by 830. I had told myself before dinner, that I would have two beers, and be at work early, hoping for 8, thinking it would probably be 8:15.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100301', 'instruction', 'I woke up in severe pain which had become all too common. I couldn''t open my eyes. An attempt to stand was met with icy hot needles in my brain; I collapsed. Watching my drool inch across the hardwood, I considered the remedy I had taken for what was becoming countless days prior; I longed for the rye whiskey on my windowsill.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100302', 'instruction', 'I remembered the promises I had made: to my co-workers, to my employees, to the people who depended on me. I remembered the times I had been sent to the hospital for alcohol poisoning, and all the promises I had made to my Mother. I called Alex, who had done so much for me already, and told her I wasn''t going to make it to work that day. No way.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100303', 'instruction', 'That is when I realized that nothing I said mattered. Every promise I had made, I was breaking it. Both the spoken and the unspoken. Here I was, doing what I wanted to do, and throwing it all away. I wallowed for a bit on all the people I was letting down. But I had done that before. It got me nowhere. I thought about slowing down my drinking, but I had tried who knows how many times, hell, just last night was supposed to be slow.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100304', 'instruction', 'When I finally pulled my face off the floor and crawled to the sink, I knew I had to do something I hadn''t ever done before. I stared myself in the eyes, unsure of what would happen next. That''s when I knew. I had to take myself seriously.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100305', 'instruction', 'I had dreams, ambitions, goals, and more. Yet so far, I had let those sit idly by while I did as I pleased. No more. I told myself I would never drink again. I told myself that it was just the start. I made a promise to myself which I live by to this day. I promised accountability. Whatever challenge should come, I vowed to face it. I vowed to overcome. I promised to be the person I knew I was meant to be.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100306', 'instruction', 'That day wasn''t the first time I had made promises to myself. So, I vowed to take myself seriously. I can still see my face. The most resolute I had ever been, and terrified of what was to come. In those eyes I see relief. Finally, I was going to take myself seriously, and no one could stop me.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100307', 'instruction', 'Since that day, my entire life has changed. I have been and am sober. I quit smoking. I revolutionized my diet. I started working out regularly. I have found time for my spirituality. I fell in love. I moved to another continent. I started a business. I take myself seriously.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100308', 'instruction', 'Overcoming addiction, changing your life, creating your story, standing up for yourself, surmounting your fears, starting your next project, building your dreams, no matter what you plan on doing next, TAKE YOURSELF SERIOUSLY. Every day. Never stop. This is grit. Daily sacrifice for long term gain.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100309', 'instruction', 'I don''t mean to wear nice clothes and look people in the eye (although those are useful habits for networking). I mean, look yourself in the mirror and hold yourself accountable. Every single day. No one else can tell you what you need to do. No one else can do the things you need to do. I am giving you all the help you need to live a more fulfilled life. The rest is up to you. You are your greatest enemy and your greatest ally. Choose to be your ally. Every day. No matter how hard it is. Don''t lie to yourself, you''ll know. Take yourself seriously.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100310', 'instruction', 'Do the things you know you need to do.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100311', 'instruction', 'Who do you look up to? Who is your role model? Why?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100312', 'instruction', 'What do you admire about that person (or those people)? What characteristics do they have that make them special?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100313', 'instruction', 'What bad habits do you need to quit?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100314', 'instruction', 'What are the good habits you need to start?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100315', 'instruction', 'What promises do you need to keep? To yourself? To those you care about?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100316', 'instruction', 'What has stopped you from keeping those promises?', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100317', 'instruction', 'Choose an order to accomplish these. If you need, start with the easy ones and work up. No matter what, choose one to complete as soon as you possibly can. Start building active trust in yourself.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100318', 'instruction', 'Forgive yourself when you mess up. If you fall off the horse, focus on getting back on the horse, not on the fact that you fell off. Forgive. Take yourself seriously, if you don''t, who will? Next.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100319', 'heading', 'Mindfulness', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100320', 'instruction', 'Mindfulness is rooted in the eastern practice of meditation. I am not asking you to chant mantras or do yoga unless that is what you want to do. What I am talking about is discipline for your mind. Silencing your mind is difficult. Try thinking about nothing for the next 5 minutes.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100321', 'instruction', '…', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100322', 'instruction', 'Too hard? How about only thinking about your breath for the next 2 minutes.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100323', 'instruction', '…', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100324', 'instruction', 'How did you do? Did your mind wander? It is okay if it did. The important thing is practicing the act of coming back to silence. Practice batting down thoughts and distractions. The world is more distracting today than it ever has been. Grabbing attention is a trillion dollar business.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100325', 'instruction', 'So, why is this important? I am glad you asked.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100326', 'instruction', 'Your brain is just like every other muscle; the parts you exercise get stronger. Mindfulness uses emotional intelligence. Emotional intelligence is otherwise known as, keeping a level head in the face of animosity. Psychologists have shown this works, neurologists agree, and some cultures have been practicing this basic technique for thousands of years.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100327', 'instruction', 'Training your brain to focus, to stay calm, and to shut out distractions will improve your soft skills, make you more able to communicate with others, improve your mental energy, and make you generally grittier.', 1, 1);
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100328', 'instruction', 'You can spend as much or as little time on this as you think necessary. I highly suggest taking it seriously.', 1, 1);
