INSERT INTO competency_levels (id, competency_id, level, description, job_context) VALUES ('cl-0702', 'comp-07', '2', 'Follows up during and after delivery to ensure needs are met. Keeps clients informed about progress and changes. Prioritizes and addresses client issues appropriately.', 'Individual contributors with some experience');
