INSERT INTO competencies (id, name, definition, category, sort_order, relevant_modules) VALUES ('comp-05', 'Resource Management', 'Understanding what people, money, and operational capacity you have available—and making smart decisions about how to deploy them for maximum impact.', 'delivery', '5', '["2.5"]');
