INSERT INTO competencies (id, name, definition, category, sort_order, relevant_modules) VALUES ('comp-08', 'Interpersonal Awareness', 'Reading people accurately—hearing what''s said and unsaid, understanding motivations, and navigating cultural differences with sensitivity. Includes managing your own reactions under pressure.', 'interpersonal', '2', '["2.5"]');
INSERT INTO competencies (id, name, definition, category, sort_order, relevant_modules) VALUES ('comp-09', 'Persuasion', 'Moving people toward your position through honest, respectful means. This involves understanding what others care about and framing your ideas in ways that resonate with them.', 'interpersonal', '3', '["2.5"]');
INSERT INTO competencies (id, name, definition, category, sort_order, relevant_modules) VALUES ('comp-10', 'Negotiation', 'Working toward outcomes where everyone walks away with something. This ranges from basic give-and-take to orchestrating complex multi-party agreements.', 'interpersonal', '4', '["2.5"]');
INSERT INTO competencies (id, name, definition, category, sort_order, relevant_modules) VALUES ('comp-11', 'Organizational Savvy', 'Understanding how power and decisions actually flow in organizations—who influences whom, what the unwritten rules are, and how to navigate formal and informal structures effectively.', 'interpersonal', '5', '["2.5"]');
INSERT INTO competencies (id, name, definition, category, sort_order, relevant_modules) VALUES ('comp-12', 'Talent Development', 'Creating conditions for people to grow. This includes sharing knowledge, giving useful feedback, delegating meaningfully, and building environments where learning happens naturally.', 'strategic', '1', '["2.5"]');
INSERT INTO competencies (id, name, definition, category, sort_order, relevant_modules) VALUES ('comp-13', 'Mission Alignment', 'Connecting your work to larger organizational goals and acting in ways that advance the collective mission, even when it means deprioritizing personal preferences.', 'strategic', '2', '["2.5"]');
INSERT INTO competencies (id, name, definition, category, sort_order, relevant_modules) VALUES ('comp-14', 'Relationship Building', 'Cultivating and maintaining connections with people who matter for long-term success—inside and outside your organization. Real relationships, not just transactional contacts.', 'strategic', '3', '["2.5"]');
INSERT INTO competencies (id, name, definition, category, sort_order, relevant_modules) VALUES ('comp-15', 'Strategic Thinking', 'Seeing the big picture—how trends, opportunities, threats, and resources connect. This means linking daily decisions to long-term direction and helping others see that connection too.', 'strategic', '4', '["2.5"]');

