INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100085', 'instruction', '2. These are the remaining characteristics, strengths, or weaknesses which match your MBTI type, but you didn''t select above. Reflect and explain why they are false.', '1', 'True');
