INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100251', 'instruction', 'What family relationships have had particularly significant impacts upon your beliefs?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100252', 'instruction', 'Do you have any elderly family members you care for or support? How does this shape your needs?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100253', 'instruction', 'Do you have any children you care for or support? How does this shape your needs?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100254', 'instruction', 'Do you have any family members or stories which inspire you? If so, give details.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100255', 'instruction', 'What are the most significant lessons your family has taught you?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100256', 'heading', 'Community', '1', 'True');
