INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100258', 'instruction', 'Are you involved in any organizations, religious groups, neighborhood associations, group of friends, gaming club, or other regularly organized social events?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100259', 'instruction', 'Why are you involved in those groups?', '1', 'True');
