INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100261', 'instruction', 'Is there anything that might cause you to leave your community?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100262', 'heading', 'World', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100263', 'instruction', 'Are there any worldly issues that are important to you? What are they?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100264', 'instruction', 'What are some lessons people should learn from history?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100265', 'instruction', 'Are there any worldly problems which you feel the need to help solve?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100266', 'instruction', 'Have you ever traveled outside of your country? Your continent?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100267', 'instruction', 'What have you learned from other cultures of the world?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100268', 'instruction', 'What do you think the world will look like in 10 years?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100269', 'instruction', '… 50 years?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100270', 'instruction', '… 100 years?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100271', 'heading', 'Self', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100272', 'instruction', 'They say that you are your own hardest critic. Critique yourself. What can you do better?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100273', 'instruction', 'What are your strengths?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100274', 'instruction', 'Do you have an idea or memory which makes you feel most like yourself? If so, describe it.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100275', 'instruction', 'What do you love about yourself?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100276', 'instruction', 'What major life events have shaped you? Briefly describe them.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100277', 'instruction', 'What is your greatest gift?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100278', 'instruction', 'Are you willing to do what needs to be done?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100279', 'heading', 'Module 5 – Health', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100280', 'instruction', 'We will focus on mental health. Physical and Spiritual health are both important, but I feel it is best for you to find answers to questions you may have about those topics from another source. People from all walks of life can benefit from mental health.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100281', 'instruction', 'I am going to address three aspects of mental health: achievement, fortitude, and emotional intelligence.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100282', 'instruction', 'In all three, the lesson is discipline and practice. The goal is not perfection. The goal is active practice.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100283', 'instruction', 'Bill Burnett describes five mindsets which are imperative to our cause. Curiosity, bias to action, reframing, awareness, and radical collaboration.¹ Mastering these mindsets and living them will lead you to opportunity.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100284', 'heading', 'Sharpening', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100285', 'instruction', 'Achievement is characterized by the things that we can do. Otherwise known as our skills. In previous modules we described our skills, our mastery over them, whether they give or take energy from us, and how captivated we are by them.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100286', 'instruction', 'Whether it is for our own pleasure, to further our working cause, or to give us a competitive edge, it is crucial to be a student of life, for life. Everybody should always be improving themselves in one way or another.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100287', 'instruction', 'The post-industrial economy is based on specialization. Pick a transferable skill or a knowledge which sets you apart from others, an expertise. If you do not have one, your duty is to make one of your skills an expertise.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100288', 'instruction', 'Additional practice, above and beyond what is needed for any of the work that you do is self-improvement for the sake of self-improvement. Sharpen your skills to make you a better candidate than your competition.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100289', 'instruction', 'You will want to start as soon as possible. Today is best; right now is even better.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100290', 'instruction', 'Choose a skill to practice.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100291', 'instruction', 'Dedicate at least 15 minutes of singular focus to improving that skill every single day.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100292', 'instruction', 'Practice the skill every single day. Don''t stop.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100293', 'instruction', 'If you grow tired of practicing this skill, reach a level of mastery, or otherwise, choose another skill and repeat the process. This is the never-ending task of self-betterment.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100294', 'heading', 'Grittiness', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100295', 'instruction', 'Grit describes the mental fortitude to work tirelessly for rewards which are either abstract or far removed, often both.⁶ I am a proponent of working smarter, not harder, but there is no replacement for diligence, perseverance, and drive. You absolutely must take yourself seriously. Even if your profession is in comedy, be a comic, not a joke.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100296', 'instruction', 'To get this message across, I have decided to share a personal story.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100297', 'heading', 'Take Yourself Seriously', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100298', 'instruction', 'On April 1st, 2016, I woke up at 3 pm. I had slept through two strategy meetings, an interview, training, and was supposed to be getting ready to go out knocking doors for the next 5 hours. I called my campaign director, she was furious, but more, she was sad.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100299', 'instruction', 'Four months ago she had put her name on the line to bring me in to direct a field canvass that had, until that point, no direction and no hope. Enthusiastic and passionate I left everything else behind to join her. We had been working 60+ hours each week to get going and had finally started to gain some momentum.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100300', 'instruction', 'Tuesday, March 31st, we went to dinner and then to a local bar to watch some music. Long after my co-worker went home, I was the last patron in the bar, finishing something past my 20th drink, around 4 am. I had promised to be at work by 830. I had told myself before dinner, that I would have two beers, and be at work early, hoping for 8, thinking it would probably be 8:15.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100301', 'instruction', 'I woke up in severe pain which had become all too common. I couldn''t open my eyes. An attempt to stand was met with icy hot needles in my brain; I collapsed. Watching my drool inch across the hardwood, I considered the remedy I had taken for what was becoming countless days prior; I longed for the rye whiskey on my windowsill.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100302', 'instruction', 'I remembered the promises I had made: to my co-workers, to my employees, to the people who depended on me. I remembered the times I had been sent to the hospital for alcohol poisoning, and all the promises I had made to my Mother. I called Alex, who had done so much for me already, and told her I wasn''t going to make it to work that day. No way.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100303', 'instruction', 'That is when I realized that nothing I said mattered. Every promise I had made, I was breaking it. Both the spoken and the unspoken. Here I was, doing what I wanted to do, and throwing it all away. I wallowed for a bit on all the people I was letting down. But I had done that before. It got me nowhere. I thought about slowing down my drinking, but I had tried who knows how many times, hell, just last night was supposed to be slow.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100304', 'instruction', 'When I finally pulled my face off the floor and crawled to the sink, I knew I had to do something I hadn''t ever done before. I stared myself in the eyes, unsure of what would happen next. That''s when I knew. I had to take myself seriously.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100305', 'instruction', 'I had dreams, ambitions, goals, and more. Yet so far, I had let those sit idly by while I did as I pleased. No more. I told myself I would never drink again. I told myself that it was just the start. I made a promise to myself which I live by to this day. I promised accountability. Whatever challenge should come, I vowed to face it. I vowed to overcome. I promised to be the person I knew I was meant to be.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100306', 'instruction', 'That day wasn''t the first time I had made promises to myself. So, I vowed to take myself seriously. I can still see my face. The most resolute I had ever been, and terrified of what was to come. In those eyes I see relief. Finally, I was going to take myself seriously, and no one could stop me.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100307', 'instruction', 'Since that day, my entire life has changed. I have been and am sober. I quit smoking. I revolutionized my diet. I started working out regularly. I have found time for my spirituality. I fell in love. I moved to another continent. I started a business. I take myself seriously.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100308', 'instruction', 'Overcoming addiction, changing your life, creating your story, standing up for yourself, surmounting your fears, starting your next project, building your dreams, no matter what you plan on doing next, TAKE YOURSELF SERIOUSLY. Every day. Never stop. This is grit. Daily sacrifice for long term gain.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100309', 'instruction', 'I don''t mean to wear nice clothes and look people in the eye (although those are useful habits for networking). I mean, look yourself in the mirror and hold yourself accountable. Every single day. No one else can tell you what you need to do. No one else can do the things you need to do. I am giving you all the help you need to live a more fulfilled life. The rest is up to you. You are your greatest enemy and your greatest ally. Choose to be your ally. Every day. No matter how hard it is. Don''t lie to yourself, you''ll know. Take yourself seriously.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100310', 'instruction', 'Do the things you know you need to do.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100311', 'instruction', 'Who do you look up to? Who is your role model? Why?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100312', 'instruction', 'What do you admire about that person (or those people)? What characteristics do they have that make them special?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100313', 'instruction', 'What bad habits do you need to quit?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100314', 'instruction', 'What are the good habits you need to start?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100315', 'instruction', 'What promises do you need to keep? To yourself? To those you care about?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100316', 'instruction', 'What has stopped you from keeping those promises?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100317', 'instruction', 'Choose an order to accomplish these. If you need, start with the easy ones and work up. No matter what, choose one to complete as soon as you possibly can. Start building active trust in yourself.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100318', 'instruction', 'Forgive yourself when you mess up. If you fall off the horse, focus on getting back on the horse, not on the fact that you fell off. Forgive. Take yourself seriously, if you don''t, who will? Next.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100319', 'heading', 'Mindfulness', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100320', 'instruction', 'Mindfulness is rooted in the eastern practice of meditation. I am not asking you to chant mantras or do yoga unless that is what you want to do. What I am talking about is discipline for your mind. Silencing your mind is difficult. Try thinking about nothing for the next 5 minutes.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100321', 'instruction', '…', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100322', 'instruction', 'Too hard? How about only thinking about your breath for the next 2 minutes.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100323', 'instruction', '…', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100324', 'instruction', 'How did you do? Did your mind wander? It is okay if it did. The important thing is practicing the act of coming back to silence. Practice batting down thoughts and distractions. The world is more distracting today than it ever has been. Grabbing attention is a trillion dollar business.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100325', 'instruction', 'So, why is this important? I am glad you asked.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100326', 'instruction', 'Your brain is just like every other muscle; the parts you exercise get stronger. Mindfulness uses emotional intelligence. Emotional intelligence is otherwise known as, keeping a level head in the face of animosity. Psychologists have shown this works, neurologists agree, and some cultures have been practicing this basic technique for thousands of years.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100327', 'instruction', 'Training your brain to focus, to stay calm, and to shut out distractions will improve your soft skills, make you more able to communicate with others, improve your mental energy, and make you generally grittier.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100328', 'instruction', 'You can spend as much or as little time on this as you think necessary. I highly suggest taking it seriously.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100329', 'instruction', 'Spend between 5 and 20 minutes a day practicing mindfulness. Focus on your breath. Push out all distractions. Silence your inner voice for a period each and every day.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100330', 'instruction', 'Some people do this while completing thoughtless tasks, but it is most effective when practiced on its own. Do what works best for you.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100331', 'heading', 'Routine', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100332', 'instruction', 'Routine is the final point of Part 1. A routine is the manifestation of discipline. Whether you realize it or not, you have a routine. Humans are creatures of habit, and that will never change.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100333', 'instruction', 'My task for you is to consider your routine intentionally. Do you wander through your day from one thing to the next? Or is there a method to your day?', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100334', 'instruction', 'Either way, you now have at least three tasks which demand your attention every day.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100335', 'instruction', 'Tracking Flow – tracking your daily activities, how they affect your energy, and how engaged you are in them.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100336', 'instruction', 'Sharpening your sword – choosing a useful and enjoyable skill to hone, practice, and sharpen as a way to stand out from your peers and, more importantly, to better yourself.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100337', 'instruction', 'Mindfulness – set aside a short amount of time to practice quieting your mind each day.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100338', 'instruction', 'Finally, remember the five mindsets every day.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100339', 'instruction', 'Curiosity', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100340', 'instruction', 'Bias to action', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100341', 'instruction', 'Reframing', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100342', 'instruction', 'Awareness', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100343', 'instruction', 'Radical collaboration', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100344', 'instruction', 'It takes about five weeks to break old habits and build new ones. If you start now, exercising your brain will become a part of your daily routine before you finish the course.', '1', 'True');
