INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100234', 'instruction', 'Shade the boxes according to the energy and focus of the activity.', '1', 'True');
INSERT INTO content_blocks (id, content_type, content, version, is_active) VALUES ('100235', 'instruction', 'Are there any activities which you find both highly energizing and highly captivating?', '1', 'True');
